mapper.insert:
INSERT INTO VOTE_ITEM ( ITEM_NO, ITEM_NAME, ORIGIN_NAME, CHANGE_NAME, ITEM_COUNT, VOTE_NO ) SELECT SEQ_INO.NEXTVAL, A.* FROM(
SELECT #{item.itemName} AS ITEM_NAME, #{item.originName} AS ORIGIN_NAME, #{item.changeName} AS CHANGE_NAME, 0 AS ITEM_COUNT, (SELECT MAX(VOTE_NO) FROM VOTE) AS VOTE_NO FROM DUAL
) A

mapper.select:
SELECT COUNT(*)
  FROM VOTE_MEMBER
 WHERE VOTE_NO = #{voteNo}
   AND USER_NO = #{userNo}

SELECT COUNT(*)
  FROM VOTE_MEMBER
 WHERE VOTE_NO = #{voteNo}
